import "./change-pages-banner.js";
import "./change-page-info.js";

class fanpage extends PolymerElement {
    static get properties() {
        return {

        };
    }

    constructor() {
        super();
    }
    ready() {
        super.ready();
        this.id = this.getAttribute('id');

        let shadow = this.shadowRoot;
        shadow.querySelector('change-pages-banner').id = this.id;
        shadow.querySelector('change-pages-banner').run();
        shadow.querySelector('change-page-info').id = this.id;
        shadow.querySelector('change-page-info').run();
    }

    static get template() {
        // Template getter must return an instance of HTMLTemplateElement.
        // The html helper function makes this easy.
        return html `
        <style include="main-style></style>
    <change-pages-banner></change-pages-banner>
    <change-page-info></change-page-info>
    `;
    }
}

// Register the element with the browser.
customElements.define('change-fanpage', fanpage);