import { PolymerElement, html } from 'https://d25k6mzsu7mq5l.cloudfront.net/node_modules/@polymer/polymer/polymer-element.js';
import 'https://d25k6mzsu7mq5l.cloudfront.net/node_modules/@vaadin/vaadin-dialog/vaadin-dialog.js';
import {Dazzle} from 'https://d25k6mzsu7mq5l.cloudfront.net/node_modules/@dazzle/dz-dazzle/dz-library.js';

class dzElm extends PolymerElement {
  static get properties () {
    return {
    
    };
  }

  constructor() {
    super();

  }
  ready() {
      super.ready();


}

  static get template () {
    // Template getter must return an instance of HTMLTemplateElement.
    // The html helper function makes this easy.
    return html`
    <style>
    @media only screen and (min-width: 768px){
        #left {
            width: 54%;
            float: left;
        }
        #right {
            width: 36%;
            margin-left: 1%;
            margin-right: 4%;
            float: left;
        }
    }

    #left {
        margin-left: 4%;
        margin-right: 1%;
        box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
        height: 500px;
    }

    #right {
        margin-left: 4%;
        margin-right: 1%;
    }
    
    textarea{
        margin: 2%;
        border: 1px solid black;
        width: 95%;
        height: 450px;
    }            
    
    img{
        width: 100%;
    }
</style>
<style include="icon-styles"></style>
<div id="container">
    <div id="left">
        <textarea placeholder="Content"></textarea>
    </div>

    <div id="right">
        <dz-block block="ads">
            <div id="ad1" class="ad">
                <img src="img/ad.jpeg" alt="Alternate Text" />
            </div>
            <div id="ad2" class="ad">
                <img src="img/ad.jpeg" alt="Alternate Text" />
            </div>
            <div id="ad3" class="ad">
                <img src="img/ad.jpeg" alt="Alternate Text" />
            </div>
        </dz-block>

    </div>
</div>
    `;
  }
}

// Register the element with the browser.
customElements.define('change-tag', dzElm);

