async getMyAllStock(ownerId){
    let that = this;
    let dbUrl = "https://search-dazzle-n4kjr6eecpjbne65ypunb42isu.ap-southeast-1.es.amazonaws.com/change.product/_count";
    let params = {
        "query": {
            "bool": {
                "must":[
                    {"match":{"isFree":false}},
                    {"match":{"ownerId":ownerId}}
                ]
              
            }
          }
    };
    await new Promise((resolve, reject) => {
        Dazzle.postData(dbUrl,params).then(res=>{
                console.log('Max Price',res.count);
                that.goodsNum = res.count;
               resolve(res.count);            
        },err=>{
            that.goodsNum = 0;
            resolve(0);
        });              
    }); 
}

