reAlias(i){
    let alias = this.user['username'];
    alias = alias.replace(/^\s+|\s+$/gm,'')
    alias = alias.replace(/ /gm,'-');
    alias = alias.replace(/\//gm,'-');
    if (i>0)
        alias = alias+'-'+i;
    this.userManager.matchData({"alias":alias}).then(res=>{
        if (!res.length)
            this.user['alias'] = alias;                    
        else{
            i++;
            this.reAlias(i);
        }
        console.log('Change Alias',res,res.length);
    });
}
