myFavor(e){
    let target = e.target;
    this.shadowRoot.querySelectorAll('.product-button').forEach(item=>{
        item.classList.remove('active');
    });
    target.classList.add('active');
    Dazzle.dzFire('my-favor',{
        detail:{
            'ownerId': this.user['id'],
            'items': this.user['likeProduct']
        }
    });
}

myOrderHistory(e){
    let target = e.target;
    this.shadowRoot.querySelectorAll('.product-button').forEach(item=>{
        item.classList.remove('active');
    });
    target.classList.add('active');
    Dazzle.dzFire('my-order-history',{
        detail:{
            'ownerId': this.user['id']
        }
    });
}
myAll(e){
    try {
        let target = e.target;
        this.shadowRoot.querySelectorAll('.product-button').forEach(item=>{
            item.classList.remove('active');
        });
        target.classList.add('active');
        Dazzle.dzFire('my-all',{
            detail:{
                'ownerId': this.user['id']
            }
        });

    }   catch(e){
        console.log('err',e);
    }

} 
myStock(e){
    let target = e.target;
    this.shadowRoot.querySelectorAll('.product-button').forEach(item=>{
        item.classList.remove('active');
    });
    target.classList.add('active');
    Dazzle.dzFire('my-stock',{
        detail:{
            'ownerId': this.user['id']
        }
    });
}

_addTags(e) {
    let myTags = this.shadowRoot.querySelector('#myTags');
    let val = this.shadowRoot.querySelector('#inputTags').value;
    // console.log('Key Down', e);

    if (e.code === "Enter") {
        this.userManager.getDataByCache(val).then(item=>{
            myTags._addTag(val);

        },err=>{
            alert('沒有這個用戶, 請重新輸入');
        });

        this.shadowRoot.querySelector('#inputTags').value = "";
    }
    //console.log("arrayResult:", myTags.tags);
}

isFanAdmin(){

    let user = store.get('change-user');
    try {
        console.log('Is Fans Admin',this.user['fansAdmin']);

        if (!this.user.isFanpage)
         return false;
        if (user.isAdmin)
             return true;
        console.log('Is Fans Admin',this.user.fansAdmin,Dazzle.subUser);
        console.log('Is Fans Admin',this.user.fansAdmin.indexOf(Dazzle.subUser['email']));

        if (this.user.fansAdmin.indexOf(Dazzle.subUser['email'])>-1)
            return true;
 
    }catch(e){
        return false;
    }        
   

    
    return false;

}
